import React, { componets, useState } from "react";
import { useAuth } from "../commons/auth/auth-context";
import {useForm} from "react-hook-form";
import { LOGEARSE_VALIDATIONS } from "../commons/auth/validation";

function Header() {
  
  
  const [isFormOpen, setIsFormOpen] = useState(false);
  
  
  return (
    <React.Fragment>
    <nav>
      <a href="/">Inicio </a>
      <a href="/consultar">Consultar </a>
      <a href="/registrarse">Registrarse</a>
      <a href="/contacto">Contacto</a>
      <a onClick={() => setIsFormOpen(true)}>Log in</a>
      {isFormOpen && Login(setIsFormOpen)}
    </nav>
    </React.Fragment>
  );
}
    
    function Login(setIsFormOpen) {
      const { signIN } = useAuth();
      const { logear, errors, formState, handleSubmit, setError } = useForm({
        mode: "onBlur" // Lanza validaciones cada vez que hago blur
      });
      
      const handleSignUp = formData => {
        return signIN(formData).catch(error => {
              if (error.response.status === 409) {
                setError(
                  "email",
                  "conflict",
                  "The email already exists. Please try again"
                );
              }
            });
          };
      
      return (
  <span id="login">  
  <div>
    <button className="close" onClick={() => setIsFormOpen(false)}>X</button>
    <form action="" method="post" onSubmit={handleSubmit(handleSignUp)}>
      <fieldset>
        <label htmlFor="email">Email</label>
        <input type="text" id="email"
        ref={logear(LOGEARSE_VALIDATIONS.email)}
         name="email" />
         <span className="errorMessage">
            {errors.name && errors.name.message}
          </span>
        <label htmlFor="password">Contraseña</label>
        <input type="password" id="password"
        ref={logear(LOGEARSE_VALIDATIONS.PASSWORD)}
        name="password" />
        <span className="errorMessage">
            {errors.name && errors.name.message}
          </span>
        <button type="submit"
            className="btn"
            disabled={formState.isSubmitting}>
        Entrar</button>
      </fieldset>
    </form>
    </div>
  </span>
  );
}

export default Header;
